//jQuery XEncode 扩展
$.extend({
    xEncode: function( str, key ) {
        if(str=="") {
            return "";
        }
        var v = s(str, true),
            k = s(key, false);
        if(k.length<4) {
            k.length=4;
        }
        var n=v.length-1,
            z=v[n],
            y=v[0],
            c=0x86014019|0x183639A0,
            m,
            e,
            p,
            q=Math.floor(6+52/(n+1)),
            d=0;
        while (0<q--) {
            d=d+c&(0x8CE0D9BF|0x731F2640);
            e=d>>>2&3;
            for(p=0;p<n;p++) {
                y=v[p+1];
                m=z>>>5^y<<2;
                m+=(y>>>3^z<<4)^(d^y);
                m+=k[(p&3)^e]^z;
                z=v[p]=v[p]+m&(0xEFB8D130|0x10472ECF);
            }
            y=v[0];
            m=z>>>5^y<<2;
            m+=(y>>>3^z<<4)^(d^y);
            m+=k[(p&3)^e]^z;
            z=v[n]=v[n]+m&(0xBB390742|0x44C6F8BD);
        }

        function s(a,b) {
            var c=a.length,v=[];
            for(var i=0;i<c;i+=4) {
                v[i>>2]=a.charCodeAt(i)|a.charCodeAt(i+1)<<8|a.charCodeAt(i+2)<<16|a.charCodeAt(i+3)<<24;
            }
            if(b) {
                v[v.length]=c;
            }
            return v;
        }

        function l(a,b) {
            var d=a.length,c=(d-1)<<2;
            if(b) {
                var m=a[d-1];
                if((m<c-3)||(m>c))
                    return null;
                c = m;
            }
            for (var i=0;i<d;i++) {
                a[i]=String.fromCharCode(a[i]&0xff,a[i]>>>8&0xff,a[i]>>>16&0xff,a[i]>>>24&0xff);
            }
            if(b) {
                return a.join('').substring(0, c);
            } else {
                return a.join('');
            }
        }

        return l(v, false);
    }
});

//jQuery getJSON 扩展
var get = $.get;
$.extend({
    getJSON: function( url, data, callback) {
        if (url.match("srun_portal") != null || url.match("get_challenge") != null) {
            var enc = "s"+"run"+"_bx1",
                n=200,
                type=1,
                base64 = new Hashes.Base64();
            if (data.action == "login") {   //login
                $data = data;
                return jQuery.getJSON(url.replace("srun_portal", "get_challenge"), {"username": $data.username, "ip": $data.ip}, function(data) {
                    var token = "";
                    if (data.res != "ok") {
                        alert(data.error);
                        return;
                    }
                    token = data.challenge;
                    $data.info = "{SRBX1}"+base64.encode(jQuery.xEncode(JSON.stringify({"username":$data.username, "password":$data.password, "ip":$data.ip, "acid":$data.ac_id, "enc_ver":enc}), token));
                    var hmd5 = new Hashes.MD5().hex_hmac(token, data.password);
                    $data.password = "{MD5}"+hmd5;
                    $data.chksum = new Hashes.SHA1().hex(token+$data.username+token+hmd5+token+$data.ac_id+token+$data.ip+token+n+token+type+token+$data.info);
                    $data.n = n;
                    $data.type = type;
                    return get( url, $data, callback, "jsonp" );
                });
            } else if (data.action == "logout") {   //logout
                $data = data;
                return jQuery.getJSON(url.replace("srun_portal", "get_challenge"), {"username": $data.username, "ip": $data.ip}, function(data) {
                    var token = "";
                    if (data.res != "ok") {
                        alert(data.error);
                        return;
                    }
                    token = data.challenge;
                    $data.info = "{SRBX1}"+base64.encode(jQuery.xEncode(JSON.stringify({"username":$data.username, "ip":$data.ip, "acid":$data.ac_id,"enc_ver":enc}), token));
                    var str = token+$data.username+token+$data.ac_id+token+$data.ip+token+n+token+type+token+$data.info;
                    $data.chksum = new Hashes.SHA1().hex(str);
                    $data.n = n;
                    $data.type = type;
                    return get(url, $data, callback, "jsonp");
                });
            } else {
                return get( url, data, callback, "jsonp" );
            }
        }
        return get( url, data, callback, "json" );
    }
});

function GetQueryString(name)
{
    var reg = new RegExp("(^|&)"+ name +"=([^&]*)(&|$)");
    var r = window.location.search.substr(1).match(reg);
    if(r!=null)return  unescape(r[2]); return null;
}

$(function() {
    $("#form2").submit(function(e){
        e.preventDefault();
        var acid = $("#ac_id"),
            uname = $("#username"),
            pwd = $("#password"),
            uip = $("#user_ip");

        if (acid.val() == "" || acid.val() == "0") {
            alert("您打开的认证页面未经重定向，点击确定后将自动重定向到正确的页面。");
            location = "http://www.baidu.com";
            return false;
        }

        if (uip.val() == "") {
            alert("请填写IP地址");
            uip.focus();
            return false;
        }

        if (uname.val() == "") {
            alert("请填写用户名");
            uname.focus();
            return false;
        }

        if (pwd.val() == "") {
            alert("请填写密码");
            pwd.focus();
            return false;
        }

        var save_me = 0;
        if ($("input[name='save_me']").is(':checked')) {
            save_me = 1;
        }

        //login  认证
        var qData = {
            "action": "login",
            "username": $('input:radio[name="online_type"]:checked').val() == '1' ? uname.val() + '@tsinghua' : uname.val(),
            "password": pwd.val(),
            "ac_id": acid.val(),
            "ip": uip.val(),
        };
        $.getJSON('https:' + "//" + 'auth4.tsinghua.edu.cn' + "/cgi-bin/srun_portal", qData, function(data) {
           if (data.error == "ok") {
                alert("登录成功");
                return false;
            }
            if (data.error_msg.indexOf("E2616")>=0){
                alert("您的账户已余额不足，请进行缴费");
            }else {
                alert(data.error_msg || data.error);
            }
            return false;
        });
        return false;
    });

    $("#form3").submit(function(e){
        e.preventDefault();
        var uname = $("#username"),
            uip = $("#user_ip");
        //logout  注销
        var pData = {
            "action": "logout",
            "username": uname.val(),
            "ac_id": GetQueryString('ac_id'),
            "ip": uip.val()
        };
        $.getJSON('https:' + "//" + location.hostname + "/cgi-bin/srun_portal", pData, function(data) {
            if (data.error == "ok") {
                location.href = location.protocol + "//" + location.hostname;
                return false;
            }
            return false;
        });
        return false;
    });

    //phone
    $("#phone-form2").submit(function(e){
        e.preventDefault();
        $this = $(this);
        var acid = $this.find("input[name=\"ac_id\"]"),
            uname = $this.find("input[name=\"username\"]"),
            pwd = $this.find("input[name=\"password\"]"),
            uip = $this.find("input[name=\"user_ip\"]");
        if (acid.val() == "" || acid.val() == "0") {
            alert("您打开的认证页面未经重定向，点击确定后将自动重定向到正确的页面。");
            location = "http://www.baidu.com";
            return false;
        }
        if (uname.val() == "") {
            alert("请填写用户名");
            uname.focus();
            return false;
        }

        if (pwd.val() == "") {
            alert("请填写密码");
            pwd.focus();
            return false;
        }

        var save_me = 0;
        if ($this.find("input[name=\"save_me\"]").is(':checked')) {
            save_me = 1;
        }

        //login  认证
        var qData = {
            "action": "login",
            "username": uname.val(),
            "password": pwd.val(),
            "ac_id": acid.val(),
            "ip": uip.val(),
        };
        $.getJSON(location.protocol + "//" + location.hostname + "/cgi-bin/srun_portal", qData, function(data) {
            if (data.error == "ok") {
                $.cookie('access_token', data.access_token);
                $.cookie("uname", uname.val());
                $this.attr("style", "display:none;");
                $("#phone-form3").attr("style", "display:block;");
                return false;
            }
            alert(data.error_msg);
            return false;
        });
        return false;
    });

    $("#phone-form3").submit(function(e){
        e.preventDefault();
        $this = $(this);
        var uip = $this.find("input[name=\"user_ip\"]");
        //logout  注销
        var pData = {
            "action": "logout",
            "username": $.cookie("uname"),
            "ac_id": GetQueryString('ac_id'),
            "ip": uip.val()
        };
        $.getJSON(location.protocol + "//" + location.hostname + "/cgi-bin/srun_portal", pData, function(data) {
            if (data.error == "ok") {
                $.cookie('uname', null);
                $.cookie('access_token', null);
                location.href = location.protocol + "//" + location.hostname;
                return false;
            }
            return false;
        });
        return false;
    });
});
